/* eslint-disable func-style */
/* eslint-disable max-len */
/* eslint-disable require-jsdoc */
function addBlocks (Blockly) {
    const QH_SMG_COLOR = '#22c32e';

    const QH_SMG_ICO = "data:image/svg+xml;base64,";

    var qdprobotESP32_BOTH_PIN_Output_Dropdown = [
        ['.P3-21-22（IIC）','21 22'],
        ['.P4-16-17（RGB）','16 17'],
        ['.P5-18-5','18 5'],
        ['.P6-23-19','13 19'],
        ['.P7-15-2（touch)','15 2'],
        ['.P8-27-14（touch）','27 14'],
        ['.P9-12-13（touch）','12 13'],
        ['.P10-25-26','25 26']
    ];

     //数码管显示
    Blockly.Blocks.PY_qdp_esp32_display = {
        init: function () {
            this.jsonInit({
                message0: '%1',
                message1: Blockly.Msg.PY_qdp_esp32_display,
                args0: [
                    {
                        type: 'field_image',
                        src: QH_SMG_ICO,
                        width: 40,
                        height: 40
                    }
                ],
                args1: [
                    {
                        type: 'field_dropdown',
                        name: 'PIN',
                        options: qdprobotESP32_BOTH_PIN_Output_Dropdown
                    }, 
                    {
                        type: 'input_value',
                        name: 'VALUE'
                    }       
                ],
                "tooltip": "显示整数与浮点数",
                colour: QH_SMG_COLOR,
                colourTertiary: '#C0C0C0',
                extensions: ['shape_statement']
            });
        }
    };

    //数码管清屏
    Blockly.Blocks.PY_qdp_esp32_display_clear = {
        init: function () {
            this.jsonInit({
                message0: '%1',
                message1: Blockly.Msg.PY_qdp_esp32_display_clear,
                args0: [
                    {
                        type: 'field_image',
                        src: QH_SMG_ICO,
                        width: 40,
                        height: 40
                    }
                ],
                args1: [
                    {
                        type: 'field_dropdown',
                        name: 'PIN',
                        options: qdprobotESP32_BOTH_PIN_Output_Dropdown
                    }     
                ],
                "tooltip": "数码管清屏",
                colour: QH_SMG_COLOR,
                colourTertiary: '#C0C0C0',
                extensions: ['shape_statement']
            });
        }
    };

    return Blockly;
}

exports = addBlocks;


